import { Component, OnInit } from '@angular/core';
import { ProductoService } from '../producto.service';
import { Producto } from '../producto';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-producto-form',
  templateUrl: './producto-form.component.html',
  styleUrls: ['./producto-form.component.css']
})
export class ProductoFormComponent implements OnInit {

  producto: Producto = {
    nombre: '',
    precio: 0,
    disponible: false,
    id: 0
  };

  constructor(
    private productoService: ProductoService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.productoService.getProducto(+id).subscribe((data: Producto) => {
        this.producto = data;
      });
    }
  }

  saveProducto(): void {
    if (this.producto.id) {
      this.productoService.updateProducto(this.producto.id, this.producto).subscribe(() => {
        this.router.navigate(['/']);
      });
    } else {
      this.productoService.createProducto(this.producto).subscribe(() => {
        this.router.navigate(['/']);
      });
    }
  }
}
<h2>{{ producto.id ? 'Editar Producto' : 'Nuevo Producto' }}</h2>
<form (ngSubmit)="saveProducto()">
  <div>
    <label for="nombre">Nombre:</label>
    <input type="text" id="nombre" [(ngModel)]="producto.nombre" name="nombre" required>
  </div>
  <div>
    <label for="precio">Precio:</label>
    <input type="number" id="precio" [(ngModel)]="producto.precio" name="precio" step="0.01" required>
  </div>
  <div>
    <label for="disponible">Disponible:</label>
    <input type="checkbox" id="disponible" [(ngModel)]="producto.disponible" name="disponible">
  </div>
  <button type="submit">Guardar</button>
</form>
