import { Component, OnInit } from '@angular/core';
import { ProductoService } from '../producto.service';
import { Producto } from '../producto';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-producto-form',
  templateUrl: './producto-form.component.html',
  styleUrls: ['./producto-form.component.css']
})
export class ProductoFormComponent implements OnInit {

  producto: Producto = {
    nombre: '',
    precio: 0,
    disponible: false,
    id: 0
  };

  mostrarNotificacion: boolean = false; // Variable para controlar la visibilidad de la notificación

  constructor(
    private productoService: ProductoService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.productoService.getProducto(+id).subscribe((data: Producto) => {
        this.producto = data;
      });
    }
  }

  saveProducto(): void {
    if (this.producto.id) {
      this.productoService.updateProducto(this.producto.id, this.producto).subscribe(() => {
        this.mostrarNotificacion = true; // Mostrar notificación
        setTimeout(() => {
          this.mostrarNotificacion = false; // Ocultar notificación después de 3 segundos
          this.router.navigate(['/']);
        }, 3000);
      });
    } else {
      this.productoService.createProducto(this.producto).subscribe(() => {
        this.mostrarNotificacion = true; // Mostrar notificación
        setTimeout(() => {
          this.mostrarNotificacion = false; // Ocultar notificación después de 3 segundos
          this.router.navigate(['/']);
        }, 3000);
      });
    }
  }
}
<div class="card bg-dark text-white">
    <div class="card-header">
        {{ producto.id ? 'Editar Producto' : 'Nuevo Producto' }}
    </div>
    <div class="card-body">
        <form (ngSubmit)="saveProducto()">
            <div class="form-group row">
                <label for="nombre" class="col-form-label col-sm-2">Nombre:</label>
                <div class="col-sm-6">
                    <input type="text" class="form-control" [(ngModel)]="producto.nombre" id="nombre" name="nombre" required>
                </div>
            </div>
            <div class="form-group row">
                <label for="precio" class="col-form-label col-sm-2">Precio:</label>
                <div class="col-sm-6">
                    <input type="number" class="form-control" [(ngModel)]="producto.precio" id="precio" name="precio" step="0.01" required>
                </div>
            </div>
            <div class="form-group row">
                <label for="disponible" class="col-form-label col-sm-2">Disponible:</label>
                <div class="col-sm-6">
                    <input type="checkbox" class="form-check-input" [(ngModel)]="producto.disponible" id="disponible" name="disponible">
                </div>
            </div>
            <div class="form-group row">
                <div class="col-sm-6 offset-sm-2">
                    <button type="submit" class="btn btn-primary">Guardar</button>
                </div>
            </div>
        </form>
    </div>
</div>

<!-- Notificación de producto creado exitosamente -->
<div *ngIf="mostrarNotificacion" class="alert alert-success alert-dismissible fade show" role="alert">
    El producto se creó exitosamente.
    <button type="button" class="close" (click)="ocultarNotificacion()">
        <span aria-hidden="true">&times;</span>
    </button>
</div>
