import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router'; // Importa Router
import { ProductoService } from '../producto.service';
import { Producto } from '../producto';

@Component({
  selector: 'app-producto-list',
  templateUrl: './producto-list.component.html',
  styleUrls: ['./producto-list.component.css']
})
export class ProductoListComponent implements OnInit {

  productos: Producto[] = [];

  constructor(private productoService: ProductoService, private router: Router) { } // Inyecta Router

  ngOnInit(): void {
    this.productoService.getProductos().subscribe((data: Producto[]) => {
      this.productos = data;
    });
  }

  editProducto(producto: Producto): void {
    // Redirige a la página de edición de producto con el ID correspondiente
    this.router.navigate(['/productos', producto.id, 'editar']);
    console.log('Editar producto:', producto);
  }

  deleteProducto(id: number): void {
    this.productoService.deleteProducto(id).subscribe(() => {
      this.productos = this.productos.filter(producto => producto.id !== id);
    });
  }

  agregarProducto(): void {
    // Redirige al formulario para agregar un nuevo producto
    this.router.navigate(['/producto-form']);
  }
}
