import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductoService } from '../producto.service';
import { Producto } from '../producto';

@Component({
  selector: 'app-producto-editar',
  templateUrl: './producto-editar.component.html',
  styleUrls: ['./producto-editar.component.css']
})
export class ProductoEditarComponent implements OnInit {
  producto: Producto;
  mostrarAlerta: boolean = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productoService: ProductoService
  ) { }

  ngOnInit(): void {
    this.obtenerProducto();
  }

  obtenerProducto(): void {
    const id = +this.route.snapshot.paramMap.get('id');
    this.productoService.getProducto(id).subscribe(producto => this.producto = producto);
  }

  guardarCambios(): void {
    this.productoService.updateProducto(this.producto.id, this.producto).subscribe(() => {
      this.mostrarAlerta = true; // Mostrar la alerta
      // Después de 3 segundos, ocultar la alerta
      setTimeout(() => {
        this.mostrarAlerta = false;
        this.router.navigate(['/productos']);
      }, 3000);
    });
  }
}
<div class="card bg-dark text-white">
  <div class="card-header bg-primary text-white">
    Editar Producto
  </div>
  <div class="card-body">
    <form>
      <div class="form-group">
        <label for="nombre">Nombre:</label>
        <input type="text" class="form-control" id="nombre" [(ngModel)]="producto.nombre" name="nombre">
      </div>
      <div class="form-group">
        <label for="precio">Precio:</label>
        <input type="number" class="form-control" id="precio" [(ngModel)]="producto.precio" name="precio" step="0.01">
      </div>
      <div class="form-check">
        <input type="checkbox" class="form-check-input" id="disponible" [(ngModel)]="producto.disponible" name="disponible">
        <label class="form-check-label" for="disponible">Disponible</label>
      </div>
      <button class="btn btn-primary mt-3" (click)="guardarCambios()">Guardar</button>
    </form>
  </div>
</div>

<!-- Ventana de alerta -->
<div class="alert alert-success mt-3" *ngIf="mostrarAlerta" role="alert">
  Producto actualizado correctamente.
</div>
