import { Component, OnInit } from '@angular/core';
import { ProductoService } from '../producto.service';
import { Producto } from '../producto';

@Component({
  selector: 'app-producto-list',
  templateUrl: './producto-list.component.html',
  styleUrls: ['./producto-list.component.css']
})
export class ProductoListComponent implements OnInit {

  productos: Producto[] = [];

  constructor(private productoService: ProductoService) { }

  ngOnInit(): void {
    this.productoService.getProductos().subscribe((data: Producto[]) => {
      this.productos = data;
    });
  }

  deleteProducto(id: number): void {
    this.productoService.deleteProducto(id).subscribe(() => {
      this.productos = this.productos.filter(producto => producto.id !== id);
    });
  }
}
<div class="card bg-dark text-white">
  <div class="card-header">
      Lista de Productos
  </div>
  <div class="card-body">
      <table class="table table-dark">
          <thead>
              <tr>
                  <th>ID</th>
                  <th>Nombre</th>
                  <th>Precio</th>
                  <th>Disponible</th>
                  <th>Acciones</th>
              </tr>
          </thead>
          <tbody>
              <tr *ngFor="let producto of productos">
                  <td>{{ producto.id }}</td>
                  <td>{{ producto.nombre }}</td>
                  <td>{{ producto.precio }}</td>
                  <td>{{ producto.disponible ? 'Sí' : 'No' }}</td>
                  <td>
                      <button class="btn btn-primary btn-sm" (click)="editProducto(producto)">Editar</button>
                      <button class="btn btn-danger btn-sm" (click)="deleteProducto(producto.id)">Eliminar</button>
                  </td>
              </tr>
          </tbody>
      </table>
  </div>
</div>
